package com.dicoding.myeventadd.helperimport androidx.recyclerview.widget.DiffUtilimport com.dicoding.myeventadd.database.Eventclass EventDiffCallback(private val oldEventList: List<Event>, private val newEventList: List<Event>) : DiffUtil.Callback() {    override fun getOldListSize(): Int = oldEventList.size    override fun getNewListSize(): Int = newEventList.size    override fun areItemsTheSame(oldItemPosition: Int, newItemPosition: Int): Boolean {        return oldEventList[oldItemPosition].id == newEventList[newItemPosition].id    }    override fun areContentsTheSame(oldItemPosition: Int, newItemPosition: Int): Boolean {        val oldNote = oldEventList[oldItemPosition]        val newNote = newEventList[newItemPosition]        return oldNote.title == newNote.title && oldNote.description == newNote.description    }}