package com.dicoding.myeventadd.ui.insertimport android.app.Applicationimport androidx.lifecycle.ViewModelimport com.dicoding.myeventadd.database.Eventimport com.dicoding.myeventadd.repository.EventRepositoryclass EventAddUpdateViewModel(application: Application) : ViewModel() {    private val mNoteRepository: EventRepository = EventRepository(application)    fun insert(event: Event) {        mNoteRepository.insert(event)    }    fun update(event: Event) {        mNoteRepository.update(event)    }    fun delete(event: Event) {        mNoteRepository.delete(event)    }}