package com.dicoding.myeventadd.ui.mainimport android.content.Intentimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.DiffUtilimport androidx.recyclerview.widget.RecyclerViewimport com.dicoding.myeventadd.database.Eventimport com.dicoding.myeventadd.databinding.ItemEventBindingimport com.dicoding.myeventadd.helper.EventDiffCallbackimport com.dicoding.myeventadd.ui.insert.EventAddUpdateActivityimport com.dicoding.myeventadd.ui.main.EventAdapter.EventViewHolderclass EventAdapter: RecyclerView.Adapter<EventViewHolder>() {    private val listEvents = ArrayList<Event>()    fun setListEvents(listEvents: List<Event>) {        val diffCallback = EventDiffCallback(this.listEvents, listEvents)        val diffResult = DiffUtil.calculateDiff(diffCallback)        this.listEvents.clear()        this.listEvents.addAll(listEvents)        diffResult.dispatchUpdatesTo(this)    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): EventViewHolder {        val binding = ItemEventBinding.inflate(LayoutInflater.from(parent.context), parent, false)        return EventViewHolder(binding)    }    override fun onBindViewHolder(holder: EventViewHolder, position: Int) {        holder.bind(listEvents[position])    }    override fun getItemCount(): Int {        return listEvents.size    }    inner class EventViewHolder(private val binding: ItemEventBinding) : RecyclerView.ViewHolder(binding.root) {        fun bind(note: Event) {            with(binding) {                tvItemTitle.text = note.title                tvItemDate.text = note.date                tvItemDescription.text = note.description                cvItemEvent.setOnClickListener {                    val intent = Intent(it.context, EventAddUpdateActivity::class.java)                    intent.putExtra(EventAddUpdateActivity.EXTRA_EVENT, note)                    it.context.startActivity(intent)                }            }        }    }}