package com.dicoding.mynoteapps.databaseimport androidx.lifecycle.LiveDataimport androidx.room.*@Daointerface EventDao {    @Insert(onConflict = OnConflictStrategy.IGNORE)    fun insert(event: Event)    @Update    fun update(event: Event)    @Delete    fun delete(event: Event)    @Query("SELECT * from event ORDER BY id ASC")    fun getAllEvent(): LiveData<List<Event>>}